import React, { useContext, useEffect, useState } from 'react';
import { AuthContext } from './login/auth';
import './estilo/insert_hoteles.css'

function RegistrationHotel() {
  const [Email, setEmail] = useState({});
  const [Nombre, setNombre] = useState({});
  const { isLoggedAdmin } = useContext(AuthContext);
  
  const Verificacion = () => {
    if (!isLoggedAdmin) {
      window.location.href = '/login-admin';
    }
  };

  const [formData, setFormData] = useState({
    nombre: '',
    descripcion: '',
    ciudad: '',
    direccion: '',
    pais: '',
    imagenes: '',
    amenidades: ''
  });

  const handleChange = (event) => {
    const { name, value } = event.target;
  };

  const RegisterHotel = () => {
      fetch('http://localhost:8060/hotels', {
      method: 'POST',
      headers: {
      'Content-Type': 'application/json'
      },
      body: JSON.stringify(formData)
      })
      .then(response => response.json())
      .then(data => {
        console.log('Registro exitoso:', data);
        window.location.href = '/ver-hoteles';
      })
      .catch(error => {
        console.error('Error en el registro:', error);
        alert('Hotel no registrado');
      });
    }

  return (
    <div className="registration-container" onLoad={Verificacion}>
      <h2>Registro De Hoteles</h2>
      <form onSubmit={RegisterHotel} className="registration-form">
        <label>
          Nombre:
          <input
            type="text"
            name="nombre"
            value={formData.nombre}
            onChange={handleChange}
            required
          />
        </label>
        <br />
        <label>
         Descripcion:
          <input
            type="text"
            name="descripcion"
            value={formData.descripcion}
            onChange={handleChange}
            required
          />
        </label>
        <br />
        <br />
        <label>
         Direccion:
          <input
            type="text"
            name="direccion"
            value={formData.direccion}
            onChange={handleChange}
            required
          />
        </label>
        <br />
        <label>
          Ciudad:
          <input
            type="text"
            name="ciudad"
            value={formData.ciudad}
            onChange={handleChange}
            required
          />
        </label>
        <br />
        <label>
         Pais:
          <input
            type="text"
            name="pais"
            value={formData.pais}
            onChange={handleChange}
          />
        </label>
        <br />
        <label>
          Imagenes:
          <input
            type="text"
            name="imagenes"
            value={formData.imagenes}
            onChange={handleChange}
            required
          />
        </label>
        <label>
          Amenidades:
          <input
            type="text"
            name="amenidades"
            value={formData.amenidades}
            onChange={handleChange}
            required
          />
        </label>
        <br />
        <button type="submit">Registrar Hotel</button>
      </form>
    </div>
  );
}

export default RegistrationHotel;
