// auth.js
import React, { createContext, useState, useEffect } from 'react';
import Cookies from 'universal-cookie';
import jwtDecode from 'jwt-decode'; // Importar directamente jwtDecode

const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
    const [auth, setAuth] = useState({ token: null, refreshToken: null, userType: null });

    useEffect(() => {
        const token = new Cookies().get('token');
        const refreshToken = new Cookies().get('refreshToken');
        if (token && refreshToken) {
            setAuth({ token, refreshToken, userType: jwtDecode(token).type });
        }
    }, []);

    const login = (token, refreshToken, userType) => {
        const cookies = new Cookies();
        cookies.set('token', token, { path: '/' });
        cookies.set('refreshToken', refreshToken, { path: '/' });
        setAuth({ token, refreshToken, userType });
    };

    const logout = () => {
        const cookies = new Cookies();
        cookies.remove('token', { path: '/' });
        cookies.remove('refreshToken', { path: '/' });
        setAuth({ token: null, refreshToken: null, userType: null });
    };

    return (
        <AuthContext.Provider value={{ auth, login, logout }}>
            {children}
        </AuthContext.Provider>
    );
};

export { AuthContext };
